import React from 'react';
import { CartContext } from 'contexts/cart/cart.context';
import { FormattedMessage } from 'react-intl';
import Image from 'components/Image/Image';
import Button from '../Button/Button';
import InputIncDec from '../InputIncDec/InputIncDec';
import { CartIcon } from '../AllSvgIcon';
import {
  ProductCardWrapper,
  ProductImageWrapper,
  ProductInfo,
  SaleTag,
  DiscountPercent,
} from './ProductCard.style';
import { getProductQuantity, findProductIndex } from '../helpers/utility';

type ProductCardProps = {
  title: string;
  image: any;
  weight: string;
  currency: string;
  description: string;
  price: number;
  salePrice?: number;
  discountInPercent?: number;
  data: any;
  onClick?: (e: any) => void;
  onChange?: (e: any) => void;
  increment?: (e: any) => void;
  decrement?: (e: any) => void;
  cartProducts?: any;
  addToCart?: any;
  updateCart?: any;
  value?: any;
  deviceType?: any;
};

const ProductCard: React.FC<ProductCardProps> = ({
  title,
  image,
  weight,
  price,
  salePrice,
  discountInPercent,
  cartProducts,
  addToCart,
  updateCart,
  value,
  currency,
  onChange,
  increment,
  decrement,
  data,
  deviceType,
  onClick,
  ...props
}) => {
  const { add, update, products } = React.useContext(CartContext);
  const index = data && data.id ? findProductIndex(products, data.id) : -1;
  const quantity = getProductQuantity(products, index);

  const handleClick = (e: any) => {
    e.stopPropagation();
    add(e, data);
  };

  const handleUpdate = (value: number, e: any) => {
    if (index === -1 && value === 1) {
      add(e, data);
    } else {
      update(data.id, value);
    }
  };

  return (
    <ProductCardWrapper onClick={onClick} className='product-card'>
      <ProductImageWrapper>
        <Image
          url={image}
          className='product-image'
          style={{ position: 'relative' }}
          alt={title}
        />
        {discountInPercent ? (
          <>
            <DiscountPercent>{discountInPercent}%</DiscountPercent>
          </>
        ) : (
          ''
        )}
      </ProductImageWrapper>
      <ProductInfo>
        <h3 className='product-title'>{title}</h3>
        <span className='product-weight'>{weight}</span>
        <div className='product-meta'>
          <div className='productPriceWrapper'>
            {discountInPercent ? (
              <span className='discountedPrice'>
                {currency}
                {price}
              </span>
            ) : (
              ''
            )}

            <span className='product-price'>
              {currency}
              {salePrice ? salePrice : price}
            </span>
          </div>

          {quantity <= 0 ? (
            <Button
              title='Cart'
              intlButtonId='addCartButton'
              iconPosition='left'
              colors='primary'
              size='small'
              variant='outlined'
              className='cart-button'
              icon={<CartIcon />}
              onClick={e => handleClick(e)}
            />
          ) : (
            <InputIncDec
              value={quantity}
              onClick={(e: any) => {
                e.stopPropagation(onclick);
              }}
              onUpdate={(value: number, e: any) => handleUpdate(value, e)}
            />
          )}
        </div>
      </ProductInfo>
    </ProductCardWrapper>
  );
};

export default ProductCard;
