import MobileDetect from "mobile-detect";
export function useDeviceType(userAgent) {
  var md = new MobileDetect(userAgent);
  var mobile = false,
      tablet = false,
      desktop = false;

  if (md.tablet()) {
    tablet = true;
  } else if (md.mobile()) {
    mobile = true;
  } else {
    desktop = true;
  }

  return {
    mobile: mobile,
    tablet: tablet,
    desktop: desktop
  };
}
